sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	//Modified th if to return if the newValue == oldValue to avid unecessary trips to the server
	if (isLoading || newValue === '' || newValue == oldValue || g_form.isNewRecord()) {
      return;
   }

   //Intantiate the GetEmailAddress Script Include
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	//Specify the getEmail method
	getEmailAddr.addParam('sysparam_name','getEmail');
	getEmailAddr.addParam('sysparam_userID', g_form.getValue('u_requested_for'));
	jslog("u_requested_for: " + g_form.getValue('u_requested_for'));
	//Send the request to the server
	getEmailAddr.getXMLAnswer(populateEmailField);

	//When the response is back from the server
	function populateEmailField(response){
		//Extract the email address from the response, clear any value from the email field.
		//set new value in the email field
		var emailFromScriptInclude = 
			resoponse.responseXML.documentElement.getAttribute("answer");
		alert(emailFromScriptInclude);
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', emailFromScriptInclude);
	}

}
